mapper.select:
SELECT 

FROM common_category a

WHERE a.id = #{id}

SELECT 

FROM common_category a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.parent_id = #{parent.id}


		AND a.parent_ids LIKE 
			'%'||#{parentIds}||'%'
			'%'+#{parentIds}+'%'
			concat('%',#{parentIds},'%')


		AND a.objcode = #{objcode}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.objlevel = #{objlevel}


		AND a.sort = #{sort}


ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM common_category a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE common_category SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE common_category SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
