mapper.select:
SELECT 

FROM c_p2p_parserconfig a

WHERE a.id = #{id}

SELECT 

FROM c_p2p_parserconfig a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.seriesid = #{cp2pSeries.id}


		AND a.classname = #{classname}


		AND a.cronex = #{cronex}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_p2p_parserconfig a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_p2p_parserconfig SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
