mapper.select:
SELECT 

FROM c_p2p_products a

WHERE a.id = #{id}

SELECT 

FROM c_p2p_products a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.seriesid = #{cp2pSeries.id}


		AND a.sid = #{sid}


		AND a.snum = #{snum}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.detailuri = #{detailuri}


		AND a.rate = #{rate}


		AND a.platformrate = #{platformrate}


		AND a.totalrate = #{totalrate}


		AND a.wanrate = #{wanrate}


		AND a.totalmoney = #{totalmoney}


		AND a.term = #{term}


		AND a.termday = #{termday}


		AND a.termmonth = #{termmonth}


		AND a.termyear = #{termyear}


		AND a.schedule = #{schedule}


		AND a.interesttime = #{interesttime}


		AND a.repaymode = #{repaymode}


		AND a.repaydate = #{repaydate}


		AND a.istransfer = #{istransfer}


		AND a.allowtransfer = #{allowtransfer}


		AND a.transfernotice = #{transfernotice}


		AND a.ismortgage = #{ismortgage}


		AND a.isguarantee = #{isguarantee}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_p2p_products a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_p2p_products SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
